package operatorOfjava;

public class Operators {
	
		public static void main(String[] args) {
			// TODO Auto-generated method stub

			//Arithmetic op
			
			int a =11;
			int b =2;
			
			System.out.println("Arithmetic op :");
			
			System.out.println("Addtion is: " +(a+b));
			
			System.out.println("Substaction is: " +(a-b));
			
			System.out.println("Mutiplication is: " +(a*b));
		
			System.out.println("Division is: " +(a/b));
			
			System.out.println("Module is: " +(a%b));
			
			
			//Relational op
			
			int x=5;
			int y=10;	
			
			System.out.println("Relational op :");
			
		System.out.println( "o/p is: "+(x<y));
		
		System.out.println( "o/p is: "+(x>y));
		
		System.out.println( "o/p is: "+(x<=y));
		
		System.out.println( "o/p is: "+(x>=y));
		
		System.out.println( "o/p is: "+(x==y));
		
		System.out.println( "o/p is: "+(x!=y));
		
		
		//UNARY
		//++ --
		
		//POST-FIX PRI-FIX
	
		int r =10;
		
		System.out.println("Pri-Post.fix op :");
		
		System.out.println(++r);
		
		System.out.println(--r);
		
	
		System.out.println(r++);
		
		System.out.println(r);
		
		System.out.println(r--);
		
		System.out.println(r);
		
		System.out.println(r<<2);
		
		System.out.println(r>>2);
		
		}

	}

